// import logo from './logo.sv  g';
import './App.css';
import { ThemeContext, useTheme } from './store/themeContext';

function App() {
  const {state: {backgroundColor, color}, dispatch} = useTheme();
  console.log('Theme ',{backgroundColor, color})
  return (
      <div className="App">
        <h1 style={{
          color, backgroundColor
        }}>Welcome</h1>
        <button onClick={()=> dispatch('CHANGE_THEME')}>Change Theme</button>
      </div>
  );
}

export default App;
