import React, { useEffect, useState } from 'react';
import './Button.css'

const Button = props => <div>
    <input type="checkbox" id="cb1" className='form__button' /><label className='form__button__label' htmlFor="cb1">Toggle me</label>
</div>

const Button1 = props => {
    var button;
    const [rippleList, setRippleList] = useState([]);
    useEffect(() => {
        button = document.querySelector('.frm_button')
    // button.addEventListener('click', materializeEffect)

    }, [])
    function materializeEffect(event) {
        const circle = document.createElement('div')
        const x = event.clientX
        const y = event.clientY
        circle.classList.add('circle')
        circle.style.left = `${x}px`
        circle.style.top = `${y}px`
        event.target.appendChild(circle)
    }

    return <div  class="frm_button">Click Me</div>
}

export default Button1;
