import React, { useContext, useEffect, useState } from 'react';
import { UserProfileContext } from '../../store/userContext';
import firebase from "firebase/app";
import { MessageContext } from '../../store/messageContext';

const ChatBox = () => {
    const { receiver: { userID: receiverUserID, userName: receiverName } } = useContext(MessageContext);
    const { name: userName, userID } = useContext(UserProfileContext);
    const [messageList, setMessageList] = useState([]);
    const [messageText, setMessageText] = useState('');
    var db = firebase.firestore()

    useEffect(() => {
        console.log({userID, receiverUserID})
        console.log("/users/"+userID+"/conversations/"+receiverUserID+"/messages")
        if (receiverUserID) {
            db.collection("/users/"+userID+"/conversations/"+receiverUserID+"/messages").get().then((querySnapshot) => {
                const result = querySnapshot.docs.map(doc => doc.data());
                setMessageList(result);
                console.log('Receiver messages ', result)
            }).catch(err => console.log('Oops ', err))

            // db.collection("/users/"+userID+"/conversations/"+receiverUserID+"/messages").orderBy("time", "asc").onSnapshot(doc => {
            //     const result = doc.docs.map(doc => doc.data());
            //     setMessageList(result);
            // })
        }

    }, [db, receiverUserID, userID]);


    const sendMessage = () => {
        db.collection("/users/"+userID+"/conversations/"+receiverUserID+"/messages").add({
            message: messageText,
            time: new Date(),//'July 19, 2021 at 12:00:00 AM UTC+5:30'
            user: userID
        }).then((docRef) => {
            console.log("Document written with ID: ", docRef.id);
        }).catch((error) => {
            console.error("Error adding document: ", error);
        });
    }

    // console.log('ChatBox dis', messageProps)
    return <div>
        <h3>DM  </h3>
        <div className="chat-box">
            {!!messageList.length && messageList.map((el, i) => <p key={i} className={el.user === userID ? 'profile right' : 'profile left'}>
                {el.user === userID ? `${el.message} <- ${userName}` : `${receiverName} -> ${el.message}`}  { }
            </p>)}
        </div>

        {userName && <>
            <input type='text' value={messageText} onChange={e => setMessageText(e.target.value)}
                onKeyPress={e => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send ={'>'}</button>
        </>}
    </div>
}

export default ChatBox;
