import React, { useContext, useEffect, useState } from 'react';
import firebase from "firebase/app";
import { UserProfileContext } from '../../store/userContext';
import { withRouter } from "react-router";
import { MessageContext, MessageProvider } from '../../store/messageContext';
import ChatBox from './chatBox';

const ChatRoom = props => {
    const [messageText, setMessageText] = useState('');
    var db = firebase.firestore()
    const { name: userName } = useContext(UserProfileContext);
    const { dispatch } = useContext(MessageContext);
    const [messageList, setMessageList] = useState([]);
    const [userList, setUserList] = useState([]);

    useEffect(() => {
        db.collection("text").orderBy("time", "asc").get().then((querySnapshot) => {
            const result = querySnapshot.docs.map(doc => doc.data());
            setMessageList(result);
        }).catch(err => console.log('Oops ', err))

        db.collection('text').orderBy("time", "asc").onSnapshot(doc => {
            const result = doc.docs.map(doc => doc.data());
            setMessageList(result);
            // console.log('changed data ', doc.docs && doc.docs.map(el => el.data()))
        })
        db.collection('users').get().then((querySnapshot) => {
            const result = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
            setUserList(result);
            console.log('users list ', result)
        }).catch(err => console.log('Oops ', err))
    }, [db])

    const sendMessage = () => {
        db.collection("text").add({
            from: userName,
            to: 'Bob',
            message: messageText,
            time: new Date()//'July 19, 2021 at 12:00:00 AM UTC+5:30'

        }).then((docRef) => {
            console.log("Document written with ID: ", docRef.id);
        }).catch((error) => {
            console.error("Error adding document: ", error);
        });
    }

    const getChatRoom = (userId) => {
        dispatch({
            type: 'SET_RECEIVER_DETAILS',
            payload: {
                name: userId,
                userName: userId,
                userID: userId
            }
        })
        // /users/user1/conversations/user2/messages
        db.collection('/users/user1/conversations/user2/messages').get().then((querySnapshot) => {
            const result = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
            // setUserList(result);
            console.log('messages list ', result)
        }).catch(err => console.log('Oops ', err))
    }

    console.log(userList)

    return <div>
        <p>testadsadas</p>
        <h1>Welcome to csshatroom {userName}</h1>
        {/* <div className="chat-box">
            {!!messageList.length && messageList.map((el, i) => <p key={i} className={el.from === userName ? 'profile right' : 'profile left'}>
                {el.from === userName ? `${el.message} <- ${userName}` : `${el.from} -> ${el.message}`}  { }
            </p>)}
        </div> */}
        <div>
            <h3><ul>pick a contact u gotta talk</ul></h3>
            <div>{userList.map((el, i) => <p key={i} onClick={e => getChatRoom(el.id)}>{el.name}</p>)}</div>
        </div>
        {userName && <>
            <input type='text' value={messageText} onChange={e => setMessageText(e.target.value)}
                onKeyPress={e => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send ={'>'}</button>
        </>}
        <MessageProvider> 
            {/* <ChatBox /> */}
            <p>sdafsdTeest</p>
        </MessageProvider>
        {/* <input type='text' value={sender} onChange={e => setSender(e.target.value)} /> */}
    </div>
}

export default withRouter(ChatRoom);
