import React, { useEffect, useState } from 'react';
import firebase from "firebase/app";

const ChatRoom = () => {
    const [messageText, setMessageText] = useState('');
    var db = firebase.firestore()
    const [messageList, setMessageList] = useState([]);

    useEffect(() => {

    db.collection("text").get().then((querySnapshot) => {
        console.log('querySnapshot ', querySnapshot)
        // setMessageText(querySnapshot);
        debugger
        const result = querySnapshot.docs.map(doc => doc.data());
        setMessageList(result);
        // ((doc) => {
        //     console.log(doc.id, doc.data());
        // });
    });
    }, [])

    const sendMessage = () => {

        db.collection("text").add({
            from: 'Bob',
            to: 'Alice',
            message: 'Hi',
            time: "2021-07-19T10:31:02.787Z"
        })
            .then((docRef) => {
                console.log("Document written with ID: ", docRef.id);
            })
            .catch((error) => {
                console.error("Error adding document: ", error);
            });
    }


    return <div>
        <h1>Welcome to chatroom</h1>
        <div>
            {messageList.map(el => <p>{el.message}</p>)}
        </div>
        <input type='text' value={messageText} onChange={e => setMessageText(e.target.value)} />
        <button onClick={sendMessage}>Add empty message</button>
    </div>
}

export default ChatRoom;
