import React, { useContext, useEffect, useState } from 'react';
import { UserProfileContext } from '../../store/userContext';
import firebase from "firebase/app";

const ChatBox = () => {
    const { receiver: { userID: receiverUserID } } = useContext(UserProfileContext);
    const { name: userName } = useContext(UserProfileContext);
    const [messageList, setMessageList] = useState([]);
    const [messageText, setMessageText] = useState('');
    var db = firebase.firestore()

    useEffect(() => {
        console.log(userName, receiverUserID)
        db.collection("/users/"+userName+"/conversations/"+receiverUserID+"/messages").orderBy("time", "asc").get().then((querySnapshot) => {
            const result = querySnapshot.docs.map(doc => doc.data());
            setMessageList(result);
            console.log('Receiver messages ', result)
        }).catch(err => console.log('Oops ', err))

        db.collection('text').orderBy("time", "asc").onSnapshot(doc => {
            const result = doc.docs.map(doc => doc.data());
            setMessageList(result);
            // console.log('changed data ', doc.docs && doc.docs.map(el => el.data()))
        })
    }, [db, receiverUserID, userName]);


    const sendMessage = () => {
        db.collection("text").add({
            from: userName,
            to: 'Bob',
            message: messageText,
            time: new Date()//'July 19, 2021 at 12:00:00 AM UTC+5:30'

        }).then((docRef) => {
            console.log("Document written with ID: ", docRef.id);
        }).catch((error) => {
            console.error("Error adding document: ", error);
        });
    }

    // console.log('ChatBox dis', messageProps)
    return <div>
        <h3>DM  </h3>
        <div className="chat-box">
            {!!messageList.length && messageList.map((el, i) => <p key={i} className={el.from === userName ? 'profile right' : 'profile left'}>
                {el.from === userName ? `${el.message} <- ${userName}` : `${el.from} -> ${el.message}`}  { }
            </p>)}
        </div>

        {userName && <>
            <input type='text' value={messageText} onChange={e => setMessageText(e.target.value)}
                onKeyPress={e => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send ={'>'}</button>
        </>}
    </div>
}

export default ChatBox;
