import { useContext } from "react";
import { ThemeContext } from "../../store/themeContext";

const HomeComponent = props => {
    const {color, backgroundColor, dispatch} = useContext(ThemeContext);
    return <div style={{backgroundColor, width: '100%', height: 'auto', position: 'absolute'}}>
        {/* <h2 style={{color, backgroundColor}}>Welcome</h2> */}
        <img alt='welcome' src='/assets/images/welcomeImage1.jpeg' />
        <button onClick={() => dispatch({type: 'CHANGE_THEME'})}></button>
    </div>
}   

export default HomeComponent;
