import React, { useContext, useReducer } from 'react';
import { createContext } from "react";

const initialTheme = {
    backgroundColor: "black",
    color: "white"
}

export const ThemeContext = createContext(initialTheme);


export const ThemeProvider = props => {
    const [state, dispatch] = useReducer(themeReducer, initialTheme);

return <ThemeContext.Provider value={{...state, dispatch}}>
    {props.children}
</ThemeContext.Provider>
}

export const useTheme = () => {
    const [state, dispatch] = useReducer(themeReducer, initialTheme)
    //  ;
    return { ...state, dispatch }
}


const themeReducer = (state = initialTheme, action) => {
    console.log('Inside theme reducer ', action)
    switch (action.type) {
        case 'CHANGE_THEME': {
            console.log('Inside change theme')
            return {
                backgroundColor: "white",
                color: "black"
            }
        }
        default: return { ...state }
    }
}
