import React, { useContext } from 'react';
import { UserProfileContext } from '../../store/userContext';

const Login = () => {
    const { loginStatus, dispatch, name } = useContext(UserProfileContext);

    if (!loginStatus) {
         var check = true 
         if (check) {
            var userName = prompt('Kimi no nawa ', 'Alice');
            var password = prompt('Password ', 'Alice');
            check = false;
         }
        dispatch({
            type: 'USER_LOGIN',
            payload: {
                name: userName,
                loggedInTime: new Date(),
                userStatus: "Online",
                loginStatus: 'Logged In'
            }
        })
        return null;
    }

    return <h3>{name}</h3>
}

export default Login;
